mapper.select:
SELECT sn.*,
lcity.cityName as cityName,
lpro.pname as pname,
lchoose.chooseid as chooseid,
lchoose.agestart as agestart,
lchoose.ageend as ageend,
lchoose.heightlstart as heightlstart,
lchoose.heightlend as heightlend,
lchoose.educationm as educationm,
lchoose.marriagem as marriagem
from emp sn
LEFT JOIN province lpro ON lpro.provinceid = sn.provinceid
LEFT JOIN city lcity ON lcity.cityid = sn.cityid
LEFT JOIN happy_hand_choose lchoose ON lchoose.empid = sn.empid
WHERE 1=1

    AND sn.is_use = #{is_use}


    AND sn.sex = #{sex}


    AND sn.education = #{education}


    AND sn.marriage = #{marriage}


    AND sn.state = #{state}


    AND sn.rzstate1 = #{rzstate1}


    AND sn.rzstate2 = #{rzstate2}


    AND sn.rzstate3 = #{rzstate3}


    AND (sn.mobile like CONCAT('%',#{keywords},'%')  OR  sn.nickname like CONCAT('%',#{keywords},'%') )


    AND sn.company like CONCAT('%',#{company},'%')


    AND (sn.tjperson like CONCAT('%',#{tjperson},'%') )


    AND (sn.tjmobile like CONCAT('%',#{tjmobile},'%') )


ORDER  by sn.dateline DESC
limit #{index},#{size}

SELECT count(sn.empid) FROM emp sn
WHERE 1=1

    AND sn.is_use = #{is_use}


    AND sn.sex = #{sex}


    AND sn.education = #{education}


    AND sn.marriage = #{marriage}


    AND sn.state = #{state}


    AND sn.rzstate1 = #{rzstate1}


    AND sn.rzstate2 = #{rzstate2}


    AND sn.rzstate3 = #{rzstate3}


    AND (sn.mobile like CONCAT('%',#{keywords},'%')  OR  sn.nickname like CONCAT('%',#{keywords},'%') )


    AND sn.company like CONCAT('%',#{company},'%')


    AND (sn.tjperson like CONCAT('%',#{tjperson},'%') )


    AND (sn.tjmobile like CONCAT('%',#{tjmobile},'%') )

SELECT count(sn.empid) FROM emp sn
WHERE 1=1
AND sn.dateline > #{startTime}
AND sn.dateline < #{endTime}

    AND sn.rzstate2 = #{rzstate2}

SELECT sn.*, lcity.cityName as cityName, lpro.pname as pname, lchoose.chooseid as chooseid, lchoose.agestart as agestart, lchoose.ageend as ageend, lchoose.heightlstart as heightlstart, lchoose.heightlend as heightlend, lchoose.educationm as educationm, lchoose.marriagem as marriagem from emp sn LEFT JOIN province lpro ON lpro.provinceid = sn.provinceid LEFT JOIN city lcity ON lcity.cityid = sn.cityid LEFT JOIN happy_hand_choose lchoose ON lchoose.empid = sn.empid WHERE 1=1 AND sn.rzstate1 ='1' AND sn.is_use = '1' AND sn.is_manager = '0'
AND (sn.age > #{agestart} OR sn.age = #{agestart})
AND (sn.age < #{ageend} OR sn.age = #{ageend})
AND (sn.heightl > #{heightlstart} OR sn.heightl = #{heightlstart})
AND (sn.heightl < #{heightlend} OR sn.heightl = #{heightlend})
AND sn.education = '5'
AND (sn.education = '4' OR sn.education = '5')
AND (sn.education = '3' OR sn.education = '4' OR sn.education = '5')
AND (sn.education = '2' OR sn.education = '3' OR sn.education = '4' OR sn.education = '5')
AND (sn.education = '1' OR sn.education = '2' OR sn.education = '3' OR sn.education = '4' OR sn.education = '5')
AND (sn.marriage = '0' or sn.marriage = '1' or sn.marriage = '2' or sn.marriage = '3')
AND sn.marriage = #{marriagem}
AND sn.marriage = #{marriagem}
AND sn.marriage = #{marriagem}
AND sn.state = #{state}
AND sn.sex = #{sex}
limit #{index},#{size}

SELECT sn.*, lcity.cityName as cityName, lpro.pname as pname from emp sn LEFT JOIN province lpro ON lpro.provinceid = sn.provinceid LEFT JOIN city lcity ON lcity.cityid = sn.cityid WHERE 1=1 AND sn.rzstate1 ='1' AND sn.is_use = '1' AND sn.is_manager = '0'
AND (sn.age > #{agestart} OR sn.age = #{agestart})
AND (sn.age < #{ageend} OR sn.age = #{ageend})
AND (sn.heightl > #{heightlstart} OR sn.heightl = #{heightlstart})
AND (sn.heightl < #{heightlend} OR sn.heightl = #{heightlend})
AND sn.education = '5'
AND (sn.education = '4' OR sn.education = '5')
AND (sn.education = '3' OR sn.education = '4' OR sn.education = '5')
AND (sn.education = '2' OR sn.education = '3' OR sn.education = '4' OR sn.education = '5')
AND (sn.education = '1' OR sn.education = '2' OR sn.education = '3' OR sn.education = '4' OR sn.education = '5')
AND (sn.marriage = '0' or sn.marriage = '1' or sn.marriage = '2' or sn.marriage = '3')
AND sn.marriage = #{marriagem}
AND sn.marriage = #{marriagem}
AND sn.marriage = #{marriagem}
AND sn.state = #{state}
AND sn.sex = #{sex}
AND sn.likeids like CONCAT('%',#{likeids},'%')
AND sn.mobile = #{keywords}
ORDER by sn.dateline DESC limit #{index},#{size}
